<template>
  <button class="z-button" :class="[buttonType,buttonSize]">
    <slot></slot>
  </button>
</template>
<script>
  export default{
    name:'ZButton'
}
</script>
 
<script setup>
  import { computed } from 'vue'
  const props = defineProps({
    type:{
      required:false,
      type:String,
      default:'default'
}, size:{
      required:false,
      type:String,
      default:'default'
} })
  const buttonType = computed(() => 'z-button--'+props.type)
  const buttonSize = computed(() => 'z-button-'+props.size)
</script>
 